import React, {useState , useEffect}from 'react';
import './PinPass.css'

const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

const recognition = new SpeechRecognition();

recognition.start();


function PinPass() {

  const voiceCommands = () => {
    recognition.onstart= () =>{
      console.log("aca escuchan2")

    }
  recognition.onresult = (e) =>{
    let current = e.resultIndex;
    let transcript = e.results[current][0].transcript;
    console.log(transcript);
  // Línea para arreglar bug en mobile
    let mobileRepeatBug = (current === 1 && transcript === e.results[0][0].transcript);

    if(!mobileRepeatBug) {
      if(transcript.length === 4) {
        document.getElementById('final').innerHTML = transcript
      }
    }}}
//     setTimeout(() => {
//       recognition.start();
//     }, 50);
//   }
//   recognition.onspeechend = () => {
//     recognition.stop();
//     console.log('voice stopped');
//   }
// }

  useEffect(( ) =>{
    voiceCommands();
  });
  const ButtonsPin = () => {
    let numbersbuttons = 9
    for ( let i = 0 ; i <= numbersbuttons ; i++){
   document.getElementById("numbers").innerHTML +=`<button class="numbersButtons">${i}</button> `
   }}
     
     useEffect(( ) =>{
       ButtonsPin();
     });

  return (
    <div className="pin">
      <h2>Hola Rodrigo</h2>
      <h5>Introduce el PIN (número secreto) de tu tarjeta o usuario de Banca electrónica</h5>
        <p id="final"></p>
        <div className="">NO PUEDO ACCEDER</div>
        <div id="numbers"></div>
     <button type="button"   className="btn btn-info">Confirmar</button>
</div>
  );
};

export default PinPass;

